import React from 'react'
import styled from 'styled-components'

const Contact = () => {
  return (
    <Wrapper className="section-center">
      <h3>Join our Newsletter</h3>
      <div className="content">
      <p>
      Lorem ipsum dolor sit amet. Id voluptatum corporis ex dignissimos corporis ea eligendi deleniti. At debitis incidunt non laboriosam labore vel voluptas eveniet qui eaque sit vero optio. Sit deserunt laboriosam sed similique molestias qui nostrum rerum id consequatur commodi. Qui provident magnam aut quia nobis At delectus consequatur in consequatur repellendus.

      </p>
      <form className="contact-form"
      action="https://formspree.io/f/xnqllagd"
      method="POST">
        
      <input type="email" 
      className="form-input" 
      placeholder="Enter email"
      name="_replyto"/>
      <button type="submit" className="submit-btn">
      Subscribe here
      </button>
      </form>
      </div>
    </Wrapper>
  )
}
const Wrapper = styled.section`
  padding: 5rem 0;
  h3 {
    text-transform: none;
  }
  p {
    line-height: 2;
    max-width: 45em;
    color: var(--clr-grey-5);
  }
  .contact-form {
    width: 90vw;
    max-width: 500px;
    display: grid;
    grid-template-columns: 1fr auto;
  }

  .form-input,
  .submit-btn {
    font-size: 1rem;
    padding: 0.5rem 1rem;
    border: 2px solid var(--clr-black);
  }
  .form-input {
    border-right: none;
    color: var(--clr-grey-3);
    border-top-left-radius: var(--radius);
    border-bottom-left-radius: var(--radius);
  }
  .submit-btn {
    border-top-right-radius: var(--radius);
    border-bottom-right-radius: var(--radius);
  }
  .form-input::placeholder {
    color: var(--clr-black);
    text-transform: capitalize;
  }
  .submit-btn {
    background: var(--clr-primary-5);
    text-transform: capitalize;
    letter-spacing: var(--spacing);
    cursor: pointer;
    transition: var(--transition);
    color: var(--clr-black);
  }
  .submit-btn:hover {
    color: var(--clr-white);
  }
  @media (min-width: 992px) {
    .content {
      display: grid;
      grid-template-columns: 1fr 1fr;
      align-items: center;
      gap: 8rem;
      margin-top: 2rem;
    }
    p {
      margin-bottom: 0;
    }
  }
  @media (min-width: 1280px) {
    padding: 15rem 0;
  }
`

export default Contact
