import React from 'react'
import styled from 'styled-components'
import { useFilterContext } from '../context/filter_context'
import { getUniqueValues, formatPrice } from '../utils/helpers'
import { FaCheck } from 'react-icons/fa'

const Filters = () => {

  const {
    filters :{
      text,
      company,
      category,
      color,
      minprice,
      maxprice,
      price,
      shipping},
      updatefilters,clearfilters,allproducts} = useFilterContext()

      // const filtercategory = allproducts.map((cat)=>cat['company'])
      // console.log(filtercategory);

      const categories = getUniqueValues(allproducts,'category')
      const companies = getUniqueValues(allproducts,'company')
      const colors = getUniqueValues(allproducts,'colors')

      //console.log(categories);
      //console.log(companies);
      //console.log(colors);

  return (
    <Wrapper>
      <div className="content">
      <form onSubmit={(e)=>e.preventDefault()}>
        {/* search input */}
        <div className="form-input">
        <input type="text" 
          name="text"
         placeholder="search here"
         className="search-input"
         value={text}
         onChange={updatefilters}/>
        </div>

      <div className="form-control">
      <h5>Category</h5>
      <div>
        {categories.map((cat,index)=>{
          return <button key={index}
          type="button"
          className={`${category === cat.toLowerCase() ? 'active' : null}`}
          onClick={updatefilters}
          name="category">
            {cat}
          </button>
        })}
      </div>
      </div>


        <div className="form-control">
        <h5>Company</h5>
        <select name="company" 
        className="company"
        value={company}
        onChange={updatefilters}>
          {companies.map((c,index)=>{
            return <option key={index} value={c}>
              {c}
            </option>
          })}
        </select>
        </div>

        {/* colors */}

        <div className="form-control">
          <h5>Colors</h5>
          <div className="colors">
          {
            colors.map((c,index)=>{
              if(c === 'all'){
                return <button key={index}
                name="color"
                className={`${color === 'all' ? 
                  'all-btn active' : 'all-btn'}`}
                onClick={updatefilters}
                data-color='all'>
                  All
                </button>
              }
              return <button key={index}
              name="color"
              style={{background:c}}
              className={`${color === c ? 
              'color-btn active' : 'color-btn'}`}
              data-color={c}
              onClick={updatefilters}>
                  {color === c ? <FaCheck /> : null}
              </button>
            })
          }
          </div>
        </div>

          <div className="form-control">
          <h5>Price</h5>
          <p className="price">{formatPrice(price)}</p>
          <input type="range" name="price" 
          onChange={updatefilters} 
          min={minprice}
          max={maxprice}
          value={price}/>
          </div>

          <div className="form-control shipping">
            <label htmlFor="shipping">Free shipping</label>
            <input type="checkbox" name="shipping" 
            id="shipping"
            onChange={updatefilters}
            checked={shipping}/>
          </div>


      </form>
      <button type="button" 
      className="clear-btn"
      onClick={clearfilters}>Clear filters</button>
      </div>
    </Wrapper>
  )
}

const Wrapper = styled.section`
  .form-control {
    margin-bottom: 1.25rem;
    h5 {
      margin-bottom: 0.5rem;
    }
  }
  .search-input {
    padding: 0.5rem;
    background: var(--clr-grey-10);
    border-radius: var(--radius);
    border-color: transparent;
    letter-spacing: var(--spacing);
  }
  .search-input::placeholder {
    text-transform: capitalize;
  }

  button {
    display: block;
    margin: 0.25em 0;
    padding: 0.25rem 0;
    text-transform: capitalize;
    background: transparent;
    border: none;
    border-bottom: 1px solid transparent;
    letter-spacing: var(--spacing);
    color: var(--clr-grey-5);
    cursor: pointer;
  }
  .active {
    border-color: var(--clr-grey-5);
  }
  .company {
    background: var(--clr-grey-10);
    border-radius: var(--radius);
    border-color: transparent;
    padding: 0.25rem;
  }
  .colors {
    display: flex;
    align-items: center;
  }
  .color-btn {
    display: inline-block;
    width: 1rem;
    height: 1rem;
    border-radius: 50%;
    background: #222;
    margin-right: 0.5rem;
    border: none;
    cursor: pointer;
    opacity: 0.5;
    display: flex;
    align-items: center;
    justify-content: center;
    svg {
      font-size: 0.5rem;
      color: var(--clr-white);
    }
  }
  .all-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 0.5rem;
    opacity: 0.5;
  }
  .active {
    opacity: 1;
  }
  .all-btn .active {
    text-decoration: underline;
  }
  .price {
    margin-bottom: 0.25rem;
  }
  .shipping {
    display: grid;
    grid-template-columns: auto 1fr;
    align-items: center;
    text-transform: capitalize;
    column-gap: 0.5rem;
    font-size: 1rem;
  }
  .clear-btn {
    background: var(--clr-red-dark);
    color: var(--clr-white);
    padding: 0.25rem 0.5rem;
    border-radius: var(--radius);
  }
  @media (min-width: 768px) {
    .content {
      position: sticky;
      top: 1rem;
    }
  }
`

export default Filters
